button_charwizard_race.script:
function onButtonPress() local wndSelectedClass = window.spellclass.getWindows(); local sClassName = "" local nMaxSpellLevel = 9; for _,v in pairs(wndSelectedClass) do if v.toggle.getValue() == "1" then sClassName = v.name.getValue(); end end window.spells.createSpellList(0, sClassName);
if CampaignRegistry.charwizard.classes then for _,vClass in pairs (CampaignRegistry.charwizard.classes) do if vClass.name == string.lower(sClassName) then if vClass.maxspelllevel then nMaxSpellLevel = vClass.maxspelllevel; end end end end
if sClassName ~= "WARLOCK" then for i=1,nMaxSpellLevel do window["button_spelllevel_" .. i].setFrame("buttonup", 5, 5, 5, 5); end else for i=1,9 do if window["slots_available_" .. i].getValue() >0 then window["button_spelllevel_" .. i].setFrame("buttonup", 5, 5, 5, 5); end end end setFrame("buttondown", 5, 5, 5, 5); end

function onButtonPress()
	local wndSelectedClass = window.spellclass.getWindows();
	local sClassName = ""
	for _,v in pairs(wndSelectedClass) do
		if v.toggle.getValue() == "1" then
			sClassName = v.name.getValue();
		end
	end
	window.spells.createSpellList(1, sClassName);

	if CampaignRegistry.charwizard.classes then
		for _,vClass in pairs (CampaignRegistry.charwizard.classes) do
			if vClass.name == string.lower(sClassName) or vClass.spec == string.lower(sClassName) then
				if vClass.maxspelllevel then
					nMaxSpellLevel = vClass.maxspelllevel;
				end
			end
		end
	end

	if sClassName ~= "WARLOCK" then
		for i=1,nMaxSpellLevel do
			window["button_spelllevel_" .. i].setFrame("buttonup", 5, 5, 5, 5);
		end
	else
		for i=1,9 do
			if window["slots_available_" .. i].getValue() >0 then
				window["button_spelllevel_" .. i].setFrame("buttonup", 5, 5, 5, 5);
			end
		end						
	end
	if sClassName == "PALADIN" or  sClassName == "RANGER" then
		window.cantrip_button.setEnabled(false);
		window.cantrip_button.setFrame("buttondisabled", 5,5,5,5);
	else
		window.cantrip_button.setEnabled(true);
		window.cantrip_button.setFrame("buttonup", 5, 5, 5, 5);
	end
	setFrame("buttondown", 5, 5, 5, 5);						
end

function onButtonPress()
	local wndSelectedClass = window.spellclass.getWindows();
	local sClassName = ""
	for _,v in pairs(wndSelectedClass) do
		if v.toggle.getValue() == "1" then
			sClassName = v.name.getValue();
		end
	end
	window.spells.createSpellList(2, sClassName);

	if CampaignRegistry.charwizard.classes then
		for _,vClass in pairs (CampaignRegistry.charwizard.classes) do
			if vClass.name == string.lower(sClassName) then
				if vClass.maxspelllevel then
					nMaxSpellLevel = vClass.maxspelllevel;
				end
			end
		end
	end

	if sClassName ~= "WARLOCK" then
		for i=1,nMaxSpellLevel do
			window["button_spelllevel_" .. i].setFrame("buttonup", 5, 5, 5, 5);
		end
	else
		for i=1,9 do
			if window["slots_available_" .. i].getValue() >0 then
				window["button_spelllevel_" .. i].setFrame("buttonup", 5, 5, 5, 5);
			end
		end						
	end
	if sClassName == "PALADIN" or  sClassName == "RANGER" then
		window.cantrip_button.setEnabled(false);
		window.cantrip_button.setFrame("buttondisabled", 5,5,5,5);
	else
		window.cantrip_button.setEnabled(true);
		window.cantrip_button.setFrame("buttonup", 5, 5, 5, 5);
	end
	setFrame("buttondown", 5, 5, 5, 5);						
end

